SELECT id, cart_id, product_id, number, product_name, product_price, total_price, product_image, gmt_created, gmt_modified
FROM cart_item
WHERE product_id=#{productId} AND cart_id=#{cartId}

SELECT id, cart_id, product_id, number, product_name, product_price, total_price, product_image, gmt_created, gmt_modified
FROM cart_item
WHERE cart_id=#{shoppingCartId}

SELECT id, cart_id, product_id, number, product_name, product_price, total_price, product_image, gmt_created, gmt_modified
FROM cart_item
WHERE id=#{id}
