SELECT id, order_number, user_id, product_detail_id, total_price, status, gmt_created, gmt_modified
FROM `order`
WHERE order_number=#{orderNumber}

SELECT COUNT(*)
FROM `order`


        AND product_detail_id=#{productDetailId}


        AND gmt_created >= #{time}

SELECT id, order_number, user_id, product_detail_id, total_price, status, gmt_created, gmt_modified
FROM `order`


        AND product_detail_id=#{productDetailId}


        AND gmt_created >= #{time}



LIMIT #{currentIndex}, #{pageSize}
